import React, { Component } from 'react'
import { Route } from 'react-router-dom'

import './navigationButton.scss'

import IconScorecard from '../Icons/iconScorecard'
import Scorecards from '../Scorecards/index';

export type Props = {
  icon: any,
  title: string
}

export default class NavigationButton extends Component<Props> {

  state = {
    isSelected: false
  }

  static defaultProps = {
    icon: <IconScorecard />,
    title: 'Scorecards'
  }


  render () {
    return (
      <div className='navigation-button'>
        <Route path="/scorecards" component={Scorecards}>
          <div className='icon'>      
            {this.props.icon}
          </div>
          <div className='title'>
            {this.props.title}
          </div>
        </Route>
      </div>
    )
  }
}
